function getLanguageFromCookie(){return document.cookie&&document.cookie.split("; ").find((n=>n.startsWith("lang="))).split("=")[1]||"ru_ru"}function fetchTranslationFile(n){return fetch(`/assets/js/${n}.json`).then((n=>{if(!n.ok)throw new Error("Translation file not found");return n.json()})).catch((()=>(console.error("Falling back to default language"),fetch("ru_ru.json").then((n=>n.json())))))}function replaceKeysWithTranslation(n){alert("a)"),document.body.innerHTML=document.body.innerHTML.replace(/{{(.*?)}}/g,(e=>{const t=e.replace(/{{|}}/g,"");return n[t]||e}))}const currentLang=getLanguageFromCookie();fetchTranslationFile(currentLang).then(replaceKeysWithTranslation).catch(console.error);